import * as React from 'react';
import { Link } from 'react-router-dom';
import { withStyles } from '@material-ui/core';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ICard from '../../types/ICard';


interface MyProps {
    item : ICard;
    onClickDelete: any;
    onDragStart: any;
    backgroundColor: {
        backgroundColor: string
    },
    classes: {
        card: string,
        bullet: string,
        title: string,
        pos: string,
        actions: string
    }
}

interface MyState {}
interface Istyles {
    card: any;
    bullet: any;
    title: any;
    pos: any;
    actions: any;
}

const styles : Istyles = {
    card: {
        backgroundColor: 'rgba(255, 255, 255, 0.7)',
        margin: '10px 20px',
        border: '1px solid #000',
        transition: '0.4s',
        boxShadow: '2px 2px 2px 2px rgba(0,0,0,0.25)',
        maxWidth: '220px',
        display: 'inline-block',
        backgroundImage: 'url(data:image/png;base64,)',
        '&:hover': {
            boxShadow: '5px 5px 5px 5px rgba(0,0,0,0.5)'
        }
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
    actions: {
        justifyContent: 'center'
    }
}

class MyCard extends React.PureComponent<MyProps, MyState> {

    constructor(props : MyProps) {
        super(props);

    }

    render() : React.ReactNode {
        const { title, description, publishDate } = this.props.item;
        const { classes, onDragStart, item, backgroundColor } = this.props;
        return (
            <Card 
                className={classes.card}
                style={backgroundColor}
                onDragStart={(ev :  React.DragEvent<HTMLDivElement>) : any => onDragStart(ev, item) } 
                draggable={true}
            >
                <CardContent>
                    <Typography className={classes.title} color="textSecondary" gutterBottom>
                        { publishDate }
                    </Typography>
                    <Typography variant="h5" component="h2">
                        { title }
                    </Typography>
                    <Typography component="p">
                        { description }
                    </Typography>
                </CardContent>
                <CardActions className={classes.actions}>
                    <Button 
                        variant="contained" 
                        color="primary" 
                        size="small"
                        component={(props:any):any => (<Link to={"/edit/"+this.props.item.id} {...props} />)}
                    >Edit</Button>

                    <Button 
                        variant="contained" 
                        color="secondary" 
                        size="small"
                        onClick={(ev : React.MouseEvent<HTMLElement>) : void => this.props.onClickDelete(ev, item) }
                    >Delete</Button>
                </CardActions>                
            </Card>
        )
    }
}


export default withStyles(styles)(MyCard);
